import React from "react";
import { useDispatch, useSelector, useStore } from "react-redux";
import { DecermentQuantity, IncermentQuantity, RemoveCart } from "../Redux/CartSlice";
import { Spinner } from "../Components/Spinners";

const Cart = () => {
    let dispatch = useDispatch()
    const {Cart,totalQuantity,totalPrice} = useSelector(state=>state.name)
    const[loading,setLoading]  = useSta(true)
   

  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-lg-6 col-sm-12 col-md-4">
         {
            Cart.map((x)=>{
                return(
                    <>
                    
                    <div className="card text-center mt-5" style={{ width: "18rem" }}>
             <center> <img src={x.image} className="card-img-top w-50" alt="..." /></center>
              <div className="card-body">
                <p className="card-title">{x.title}</p>
                <p>$ {x.price}</p>
                <div className="d-flex justify-content-around mt-2">
                <button onClick={()=>dispatch(IncermentQuantity(x))}>+</button>
                {x.quantity}
                <button  onClick={()=>dispatch(DecermentQuantity(x))}>-</button>
                <button onClick={()=>dispatch(RemoveCart(x))} className="btn btn-danger" >delete</button>
                </div>
                
              </div>
            </div>
                    </>
                )
            })
         }
          </div>
        </div>
      </div>
    </>
  );
};

export default Cart;
