import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  DecermentQuantity,
  GetTotalCart,
  IncermentQuantity,
  RemoveCart,
} from "../Redux/CartSlice";
import { Spinner } from "../Components/Spinners";

const Cart = () => {
  let dispatch = useDispatch();
  const { Cart, totalQuantity, totalPrice } = useSelector(
    (state) => state.name
  );
  useEffect(()=>{
    dispatch(GetTotalCart())
  },[Cart])
  return (
    <>
      <div style={{ marginTop: "100px" }} className="container">
        <div className="row">
          <div className="col-lg-6 col-sm-12 col-md-4">
            {Cart.map((x) => {
              return (
                <>
                  <div
                    className="card text-center mt-5"
                    style={{ width: "18rem" }}
                  >
                    <center>
                      {" "}
                      <img
                        src={x.image}
                        className="card-img-top w-50"
                        alt="..."
                      />
                    </center>
                    <div className="card-body">
                      <p className="card-title">{x.title}</p>
                      <p>$ {x.price}</p>
                      <div className="d-flex justify-content-around mt-2">
                        <button className="btn btn-success" onClick={() => dispatch(IncermentQuantity(x))}>
                          +
                        </button>
                        {x.quantity}
                        <button onClick={() => dispatch(DecermentQuantity(x))}>
                          -
                        </button>
                        <button
                          onClick={() => dispatch(RemoveCart(x))}
                          className="btn btn-danger"
                        >
                          delete
                        </button>
                      </div>
                    </div>
                  </div>
                </>
              );
            })}
          </div>
          <div className="col-lg-6 col-sm-12 col-md-4">
            <div className=" col-sm-12">
              <div className="d-flex justify-content-around bg-dark text-light">
                <h3>Total Quantity: {totalQuantity}</h3>
                <h3>Total Price: ${totalPrice}</h3>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Cart;
