import React, { useEffect, useState } from "react";
import axios from "axios";

const Alluser = () => {
  const [data, setData] = useState([]);
  async function getUsers() {
    try {
      let res = await axios.get("http://127.0.0.1:1214/api/User/users");
      setData(res.data.users);
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(() => {
    getUsers();
  }, [data]);
  const Delete = async(id)=>{
    try {
        let res = await axios.delete(`http://127.0.0.1:1214/api/User/delete/${id}`)
        alert(res.data.Message)
    } catch (error) {
        
    }

  }
  return (
    <>
   {!data ? (
  <h1>No record found</h1>
) : (
  <table cellPadding={10} className="table table-hover container mt-5">
    <thead>
      <tr>
        <th>Name</th>
        <th>Email</th>
        <th>Password</th>
        <th>Address</th>
        <th>Phone</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
      {data.map((x) => {
        return (
          <tr key={x._id}>
            <td>{x.Name}</td>
            <td>{x.Email}</td>
            <td>{x.Password}</td>
            <td>{x.Address}</td>
            <td>{x.Phone}</td>
            <td>
              <button className="btn btn-danger" onClick={() => Delete(x._id)}>
                Delete
              </button>
              <button className="btn btn-success my-2">Edit</button>
            </td>
          </tr>
        );
      })}
    </tbody>
  </table>
)}

    </>
  );
};

export default Alluser;
